inline Gemm generate(int M, int K, int N) {
    assert(M > 0 && K > 0 && N > 0);

    Gemm data;
    data.M = M; data.K = K; data.N = N;
    data.h_A.assign(static_cast<std::size_t>(M) * K, 0.0f);
    data.h_B.assign(static_cast<std::size_t>(K) * N, 0.0f);

    // בדיוק לפי ההיגיון שביקשת:
    // A[r,c] = (r+1) + 0.01*c
    for (int r = 0; r < M; ++r) {
        for (int c = 0; c < K; ++c) {
            data.h_A[static_cast<std::size_t>(r) * K + c] =
                float(r + 1) + 0.01f * float(c);
        }
    }

    // B[r,c] = (c+1) + 0.01*r
    for (int r = 0; r < K; ++r) {
        for (int c = 0; c < N; ++c) {
            data.h_B[static_cast<std::size_t>(r) * N + c] =
                float(c + 1) + 0.01f * float(r);
        }
    }

    return data;
}

